</button>
          </div>
          </div>


        <div className="lowerSide">

        </div>
      </div>
      <div className="main">
        <div className="chats">


          {messages.map((messages,i)=>
            <div key={i} className={messages.isBot?"chat bot":"chat"}>

            <img className="chatImg"src={messages.isBot?gptImgLogo:userIcon} alt="" /><p className="txt">{messages.text}</p>

